mapper.update:
  	UPDATE AT_COMPANY_AGENT
  		SET
            CP_CODE = #{cpCode,jdbcType=VARCHAR}
    		,CP_NAME = #{cpName,jdbcType=VARCHAR}

	  	    ,CP_NAME_ALL = #{cpNameAll,jdbcType=VARCHAR}


	  		,RG_ADDRESS = #{rgAddress,jdbcType=VARCHAR}


	  		,OFFICE_ADDRESS = #{officeAddress,jdbcType=VARCHAR}

	  		,CP_OWNER = #{cpOwner,jdbcType=VARCHAR}
	  		,CP_AGENTER = #{cpAgenter,jdbcType=VARCHAR}
	  		,CP_TEL = #{cpTel,jdbcType=VARCHAR}

	  		,CP_EMAIL = #{cpEmail,jdbcType=VARCHAR}


	  		,USE_TYPE = #{useType,jdbcType=DECIMAL}

	  		,UPDATE_TIME = #{updateTime}

	  		,REMARK = #{remark,jdbcType=VARCHAR}

		WHERE
			ID=#{id}

mapper.select:
	  	SELECT
	  		 ID as id,
	  		 CP_CODE as cpCode,
	  		 CP_NAME as cpName,
	         CP_NAME_ALL as cpNameAll,
	         RG_ADDRESS as rgAddress,
	         OFFICE_ADDRESS as officeAddress,
	         CP_OWNER as cpOwner,
	         CP_AGENTER as cpAgenter,
	         CP_TEL as cpTel,
	         CP_EMAIL as cpEmail,
	         USE_TYPE as useType,
	         CREATE_TIME as createTime,
	         UPDATE_TIME as updateTime,
	         REMARK as remark
	    FROM AT_COMPANY_AGENT  
	    	WHERE 1 = 1

    		and CP_NAME = #{cpName,jdbcType=VARCHAR}


	  	    and CP_NAME_ALL = #{cpNameAll,jdbcType=VARCHAR}


	  		and RG_ADDRESS = #{rgAddress,jdbcType=VARCHAR}


	  		and OFFICE_ADDRESS = #{officeAddress,jdbcType=VARCHAR}


	  		and CP_OWNER = #{cpOwner,jdbcType=VARCHAR}


	  		and CP_AGENTER = #{cpAgenter,jdbcType=VARCHAR}


	  		and CP_TEL = #{cpTel,jdbcType=VARCHAR}


	  		and CP_EMAIL = #{cpEmail,jdbcType=VARCHAR}


	  		and USE_TYPE = #{useType,jdbcType=DECIMAL}


	  		and REMARK = #{remark,jdbcType=VARCHAR}

	    	ORDER BY CREATE_TIME DESC 
	    	limit #{page.pageSize} offset #{page.offset}

	    SELECT
	         count(*)
	    FROM AT_COMPANY_AGENT 
	    WHERE 1 = 1

    		and CP_NAME = #{cpName,jdbcType=VARCHAR}


	  	    and CP_NAME_ALL = #{cpNameAll,jdbcType=VARCHAR}


	  		and RG_ADDRESS = #{rgAddress,jdbcType=VARCHAR}


	  		and OFFICE_ADDRESS = #{officeAddress,jdbcType=VARCHAR}


	  		and CP_OWNER = #{cpOwner,jdbcType=VARCHAR}


	  		and CP_AGENTER = #{cpAgenter,jdbcType=VARCHAR}


	  		and CP_TEL = #{cpTel,jdbcType=VARCHAR}


	  		and CP_EMAIL = #{cpEmail,jdbcType=VARCHAR}


	  		and USE_TYPE = #{useType,jdbcType=DECIMAL}


	  		and REMARK = #{remark,jdbcType=VARCHAR}
