mapper.update:
  	UPDATE AT_CST_IMY
  		SET
	  	    	CP_CODE = #{cpCode}
	  	    	,CN_IDENTIFER = #{cnIdentifer}

	  	    	,IMY_FORM = #{imyForm}


	  	    	,CID = #{cid}


	  	    	,IMY_STATUS = #{imyStatus}

	  	    	,IMY_OPT = #{imyOpt}

	  	    	,IMY_REASON = #{imyReason}


	  	    	,IMY_AUDIT_RST = #{imyAuditRst}


	  	    	,IMY_RESULT = #{imyResult}


	  	    	,IMY_SUM = #{imySum}


	  	    	,PAY_TYPE = #{payType}


	  	    	,PAY_TIME = #{payTime}

	  	    	,OPERATOR = #{operator}
	  			,UPDATE_TIME = #{updateTime}

	  			,REMARK = #{remark}

		WHERE
			ID=#{id}

mapper.select:
SELECT c.ID as id, CP_CODE as cpCode, CN_IDENTIFER as cnIdentifer, IMY_FORM as imyForm , CID as cid , IMY_STATUS as imyStatus, IMY_OPT as imyOpt, IMY_REASON as imyReason, IMY_AUDIT_RST as imyAuditRst, IMY_RESULT as imyResult, IMY_SUM as imySum , PAY_TYPE as payType, PAY_TIME as payTime, OPERATOR as operator , CREATE_TIME as createTime, UPDATE_TIME as updateTime , REMARK as remark FROM AT_CST_IMY c where 1 = 1
and CP_CODE = #{cpCode}
and CN_IDENTIFER = #{cnIdentifer}
and IMY_FORM = #{imyForm}
and IMY_STATUS = #{imyStatus}
and PAY_TYPE = #{payType}
and OPERATOR = #{operator}
and REMARK like #{remark}
ORDER BY createTime DESC limit #{page.pageSize} offset #{page.offset}

 	SELECT COUNT(*) FROM AT_CST_IMY c
 		where 1 = 1

	    	and CP_CODE = #{cpCode}


	    	and CN_IDENTIFER = #{cnIdentifer}


	    	and IMY_FORM = #{imyForm}


 	    	and IMY_STATUS = #{imyStatus}


	    	and PAY_TYPE = #{payType}


 	    	and OPERATOR = #{operator}


 	    	and REMARK like #{remark}
