mapper.update:
  	UPDATE AT_CST_VISIT
  		SET
	  	    	CP_CODE = #{cpCode}
	  	    	,CN_IDENTIFER = #{cnIdentifer}

	  	    	,CN_CUSTOMER = #{cnCustomer}


	  	    	,CN_TYPE = #{cnType}


	  	    	,CN_CONTACT = #{cnContact}


	  	    	,CN_TIME = #{cnTime}


	  	    	,CN_CONTENT = #{cnContent}


	  	    	,CN_RESULT = #{cnResult}


	  	    	,OPERATOR = #{operator}

	  			,UPDATE_TIME = #{updateTime}

	  			,REMARK = #{remark}

		WHERE
			ID=#{id}

mapper.select:
SELECT c.ID as id, CP_CODE as cpCode, CN_IDENTIFER as cnIdentifer, CN_CUSTOMER as cnCustomer, CN_TYPE as cnType, CN_CONTACT as cnContact, CN_TIME as cnTime, CN_CONTENT as cnContent, CN_RESULT as cnResult, OPERATOR as operator, CREATE_TIME as createTime, UPDATE_TIME as updateTime, REMARK as remark FROM AT_CST_VISIT c where 1 = 1
and CP_CODE = #{cpCode}
and CN_IDENTIFER = #{cnIdentifer}
and LOCATE(#{cnCustomer},CN_CUSTOMER) > 0
and CN_TYPE = #{cnType}
and CN_RESULT = #{cnResult}
and OPERATOR = #{operator}
and LOCATE(#{remark},REMARK) > 0
ORDER BY createTime DESC limit #{page.pageSize} offset #{page.offset}

  	SELECT COUNT(*) FROM AT_CST_VISIT c
  		where 1 = 1

	    		and CP_CODE = #{cpCode}


 	    	and CN_IDENTIFER = #{cnIdentifer}


 	    	and LOCATE(#{cnCustomer},CN_CUSTOMER) > 0


  	    	and CN_TYPE = #{cnType}


 	    	and CN_RESULT = #{cnResult}


  	    	and OPERATOR = #{operator}


  	    	and LOCATE(#{remark},REMARK) > 0
