mapper.update:
  	UPDATE AT_FIELD_REL
  		SET
  			UPDATE_TIME = #{updateTime}
  	    	,MENU_CODE = #{menuCode}
  	    	,FID = #{fid}
  	    	,UID = #{uid}

  	    	,R_NAME = #{rname}


  	    	,PRIVILEGETYPE = #{privilegeType}


  	    	,ISUSERGROUP = #{isUserGroup}


  	    	,OPERATOR = #{operator}


  			,REMARK = #{remark}

		WHERE
			ID=#{id}

mapper.delete:
DELETE FROM AT_FIELD_REL WHERE 1 = 1 

    	and UID = #{uid}


    	and MENU_CODE in 
    	(
    		select asm.MENU_CODE from AT_SYS_MODEL asm 
    		where asm.TREE_ID = #{treeId}
    	)

mapper.select:
SELECT c.ID as id, MENU_CODE as menuCode, R_NAME as rname, PRIVILEGETYPE as privilegeType, FID as fid, UID as uid, ISUSERGROUP as isUserGroup, OPERATOR as operator, CREATE_TIME as createTime, UPDATE_TIME as updateTime, REMARK as remark FROM AT_FIELD_REL c where 1 = 1
and MENU_CODE = #{menuCode}
and LOCATE(#{rName},R_NAME) > 0
and PRIVILEGETYPE = #{privilegeType}
and FID = #{fid}
and UID = #{uid}
and LOCATE(#{remark},REMARK) > 0
ORDER BY createTime DESC limit #{page.pageSize} offset #{page.offset}

 	SELECT COUNT(*) FROM AT_FIELD_REL c
 		where 1 = 1

	    	and MENU_CODE = #{menuCode}


	    	and LOCATE(#{rName},R_NAME) > 0


	    	and PRIVILEGETYPE = #{privilegeType}


	    	and FID = #{fid}


	    	and UID = #{uid}


 	    	and LOCATE(#{remark},REMARK) > 0

SELECT c.ID as id, MENU_CODE as menuCode, R_NAME as rname, PRIVILEGETYPE as privilegeType, FID as fid, UID as uid, ISUSERGROUP as isUserGroup, OPERATOR as operator, CREATE_TIME as createTime, UPDATE_TIME as updateTime, REMARK as remark FROM AT_FIELD_REL c where 1 = 1
and UID = #{uid}
and MENU_CODE = #{menuCode}
ORDER BY createTime

SELECT asm.ID as id, asm.TREE_ID as treeId, asm.MENU_CODE as menuCode, asm.M_NAME as mname, asm.M_DESC as mdesc, asm.IS_SYSTEM as isSystem, asm.CATEGORY as category, asm.IS_CON_SHOWNUM as isConShownum, asm.CONTROL_NUM as controlNum, asm.CONTROL_PAGE as controlPage, asm.SHOW_INDEX as showIndex, asm.CREATE_TIME as createTime, asm.UPDATE_TIME as updateTime, asm.REMARK as remark FROM AT_SYS_MODEL asm, AT_FIELD_DICT afd where
afd.ID = #{dictIds}
and afd.MENU_CODE = asm.MENU_CODE
