mapper.update:
  	UPDATE AT_MODEL_FUNCTION
  		SET
  			UPDATE_TIME = #{updateTime}
  	    	,MENU_CODE = #{menuCode}
  	    	,F_NAME = #{fname}
  	    	,F_TITLE = #{ftitle}

  	    	,TABLE_INDEX = #{tableIndex}


  	    	,IS_SYSTEM = #{isSystem}


  	    	,FUN_CODE = #{funCode}


  	    	,FUN_DESC = #{funDesc}


  	    	,ID_PARENT = #{idParent}


  	    	,CONTROL_TYPE = #{controlType}


  	    	,IS_HASTAG = #{isHastag}


  	    	,CATEGORY = #{category}


  	    	,IS_CON_SHOWNUM = #{isConShownum}


  	    	,CONTROL_NUM = #{controlNum}


  	    	,CONTROL_PAGE = #{controlPage}


  	    	,SHOW_INDEX = #{showIndex}

  			,OPERATOR = #{operator}

  			,REMARK = #{remark}

		WHERE
			ID=#{id}

mapper.select:
SELECT c.ID as id, MENU_CODE as menuCode, F_NAME as fname, F_TITLE as mdesc, TABLE_INDEX as tableIndex, IS_SYSTEM as isSystem, FUN_CODE as funCode, FUN_DESC as funDesc, ID_PARENT as idParent, CONTROL_ID as controlId, CONTROL_TYPE as controlType, IS_HASTAG as isHastag, CATEGORY as category, SHOW_INDEX as showIndex, IS_CON_SHOWNUM as isConShownum, CONTROL_NUM as controlNum, CONTROL_PAGE as controlPage, CREATE_TIME as createTime, OPERATOR as operator, UPDATE_TIME as updateTime, REMARK as remark FROM AT_MODEL_FUNCTION c where 1 = 1
and MENU_CODE = #{menuCode}
and F_NAME like #{fname}
and CONTROL_TYPE = #{controlType}
and IS_HASTAG = #{isHastag}
and IS_CON_SHOWNUM = #{isConShownum}
and OPERATOR = #{operator}
and REMARK like #{remark}
ORDER BY showIndex limit #{page.pageSize} offset #{page.offset}

 	SELECT COUNT(*) FROM AT_MODEL_FUNCTION c
 		where 1 = 1

	    	and MENU_CODE = #{menuCode}


	    	and F_NAME like #{fname}


	    	and CONTROL_TYPE = #{controlType}


	    	and IS_HASTAG = #{isHastag}


 	    	and IS_CON_SHOWNUM = #{isConShownum}


	    	and OPERATOR = #{operator}


 	    	and REMARK like #{remark}
