mapper.update:
  	UPDATE AT_USER
  		SET
	  		CP_CODE = #{cpCode}
	  		,I_NAME = #{iname}
	  		,LOGIN_PWD = #{loginPwd}
	  		,USER_NAME = #{userName}
	  		,SEX = #{sex}
	  		,TELNO = #{telno}
	  		,IS_ADMIN = #{isAdmin}
	  		,OPERATOR = #{operator}
	  		,UPDATE_TIME = #{updateTime}

	  		  ,OID = #{oid}


	  		  ,I_TYPE = #{itype}


	  		  ,WORKER_NO = #{workerNo}


	  		  ,JOB_DUTY = #{jobDuty}


	  		  ,EMAIL = #{email}


	  		  ,IS_USED = #{isUsed}


	  		  ,ORDER_NUM = #{orderNum}


	  		  ,AUTH_STATE = #{authState}


	  		  ,LAST_LOGIN_DATE = #{lastLoginDate}


	  		  ,STATUS = #{status}


	  		  ,LOGIN_IP = #{loginIp}


	  		  ,LOGIN_TIME = #{loginTime}


	  		  ,LAST_OPERATOR = #{lastOperator}


	  		  ,REMARK = #{remark}

		WHERE
			ID=#{id}

mapper.select:
	SELECT
		ID as id,
		CP_CODE as cpCode,
		OID as oid,
		I_TYPE as itype,
		I_NAME as iname,
		LOGIN_PWD as loginPwd,
		USER_NAME as userName,
		SEX as sex,
		WORKER_NO as workerNo,
		JOB_DUTY as jobDuty,
		TELNO as telno,
		EMAIL as email,
		IS_USED as isUsed,
		IS_ADMIN as isAdmin,
		ORDER_NUM as orderNum,
		AUTH_STATE as authState,
		LAST_LOGIN_DATE as lastLoginDate,
		STATUS as status,
		LOGIN_IP as loginIp,
		LOGIN_TIME as loginTime,
		OPERATOR as operator,
		CREATE_TIME as createTime,
		LAST_OPERATOR as lastOperator,
		UPDATE_TIME as updateTime,
		REMARK as remark 
  FROM AT_USER 
  	WHERE 1 = 1
  	and CP_CODE = #{cpCode}
  	and OID = #{oid}
  	and I_TYPE = #{iType}
  	and I_NAME = #{iName}
  	and LOGIN_PWD = #{loginPwd}
  	and USER_NAME = #{userName}
  	and SEX = #{sex}
  	and WORKER_NO = #{workerNo}
  	and JOB_DUTY = #{jobDuty}
  	and TELNO = #{telno}
  	and EMAIL = #{email}
  	and IS_USED = #{isUsed}
  	and IS_ADMIN = #{isAdmin}
  	and ORDER_NUM = #{orderNum}
  	and AUTH_STATE = #{authState}
  	and LAST_LOGIN_DATE = #{lastLoginDate}
  	and STATUS = #{status}
  	and LOGIN_IP = #{loginIp}
  	and LOGIN_TIME = #{loginTime}
  	and OPERATOR = #{operator}
  	and LAST_OPERATOR = #{lastOperator}
  	and REMARK = #{remark}
  	ORDER BY CREATE_TIME DESC 
  	limit #{page.pageSize} offset #{page.offset}

SELECT
     count(*)
FROM AT_USER 
WHERE 1 = 1
and CP_CODE = #{cpCode}
	and OID = #{oid}
	and I_TYPE = #{iType}
	and I_NAME = #{iName}
	and LOGIN_PWD = #{loginPwd}
	and USER_NAME = #{userName}
	and SEX = #{sex}
	and WORKER_NO = #{workerNo}
	and JOB_DUTY = #{jobDuty}
	and TELNO = #{telno}
	and EMAIL = #{email}
	and IS_USED = #{isUsed}
	and IS_ADMIN = #{isAdmin}
	and ORDER_NUM = #{orderNum}
	and AUTH_STATE = #{authState}
	and LAST_LOGIN_DATE = #{lastLoginDate}
	and STATUS = #{status}
