mapper.update:
  	UPDATE ACM_SYS_ORG
  		SET
  		    CP_CODE = #{cpCode,jdbcType=VARCHAR}
            ,ORG_NAME = #{orgName,jdbcType=VARCHAR}
            
    		,ORG_DESC = #{orgDesc,jdbcType=VARCHAR}
    		
	  	    ,OID = #{oid,jdbcType=DECIMAL}
	  		,UPDATE_TIME = #{updateTime}
	  		
	  		,REMARK = #{remark,jdbcType=VARCHAR}
	  			        
		WHERE
			ID=#{id}

mapper.select:
SELECT ID as id, CP_CODE as cpCode, ORG_NAME as orgName, ORG_DESC as orgDesc, OID as oid, OPERATOR as operator, CREATE_TIME as createTime, UPDATE_TIME as updateTime, REMARK as remark FROM ACM_SYS_ORG WHERE 1 = 1
and OID = #{oid}
and ORG_NAME = #{orgName}
ORDER BY CREATE_TIME DESC limit #{page.pageSize} offset #{page.offset}

  SELECT
       count(*)
  FROM ACM_SYS_ORG
  WHERE 1 = 1
    
	    and OID = #{oid}
		
		
		and ORG_NAME = #{orgName}
