mapper.update:
  	UPDATE AT_COMPLAIN
  		SET
		  	    CP_CODE = #{cpCode}
		  	    ,CSR_IDENTIFER = #{csrIdentifer}

	  	    	,CN_FROM = #{cnFrom}


	  	    	,CN_TYPE = #{cnType}

	  	    	,CN_THEME = #{cnTheme}

	  	    	,CN_CONTENT = #{cnContent}

	  	    	,CN_OPT = #{cnOpt}

	  	    	,CN_INQUIRE = #{cnInquire}


	  	    	,CN_RESULT = #{cnResult}


	  	    	,CN_PRINCIPAL = #{cnPrincipal}


	  	    	,CN_STATUS = #{cnStatus}

	  	    	,OPERATOR = #{operator}
	  	    	,UPDATE_TIME = #{updateTime}

	  			,REMARK = #{remark}

		WHERE
			ID=#{id}

mapper.select:
SELECT c.ID as id, CP_CODE as cpCode, CSR_IDENTIFER as csrIdentifer, CN_FROM as cnFrom, CN_TYPE as cnType, CN_THEME as cnTheme, CN_CONTENT as cnContent, CN_OPT as cnOpt, CN_INQUIRE as cnInquire, CN_RESULT as cnResult, CN_PRINCIPAL as cnPrincipal, CN_STATUS as cnStatus, OPERATOR as operator, CREATE_TIME as createTime, UPDATE_TIME as updateTime, REMARK as remark FROM AT_COMPLAIN c where 1 = 1
and CP_CODE = #{cpCode}
and CSR_IDENTIFER = #{csrIdentifer}
and CN_FROM = #{cnFrom}
and CN_TYPE = #{cnType}
and LOCATE(#{cnTheme},CN_THEME) > 0
and CN_PRINCIPAL = #{cnPrincipal}
and CN_STATUS = #{cnStatus}
and OPERATOR = #{operator}
and LOCATE(#{remark},REMARK) > 0
ORDER BY createTime DESC limit #{page.pageSize} offset #{page.offset}

  	SELECT COUNT(*) FROM AT_COMPLAIN c
  		where 1 = 1

	    	and CP_CODE = #{cpCode}


 	    	and CSR_IDENTIFER = #{csrIdentifer}


 	    	and CN_FROM = #{cnFrom}


  	    	and CN_TYPE = #{cnType}


 	    	and LOCATE(#{cnTheme},CN_THEME) > 0 


 	    	and CN_PRINCIPAL = #{cnPrincipal}


  	    	and CN_STATUS = #{cnStatus}


  	    	and OPERATOR = #{operator}


  	    	and LOCATE(#{remark},REMARK) > 0
