mapper.update:
  	UPDATE AT_CSR_BASIC
  		SET
            CP_CODE = #{cpCode}
    		,CSR_TYPE = #{csrType}
	  	    ,CSR_IDENTIFER = #{csrIdentifer}
	  		
	  		,CSR_CODE = #{csrCode}
	  		
	  		
	  		,REG_ADDRESS = #{regAddress}
	  		
	  		
	  		,OFFICE_ADDRESS = #{officeAddress}
	  		
	  		
	  		,CSR_OWNER = #{csrOwner}
	  		
	  		
	  		,FINANCE_LEADER = #{financeLeader}
	  		
	  		
	  		,TAX_LEADER = #{taxLeader}
	  		
	  		,OPERATOR = #{operator}
	  		,UPDATE_TIME = #{updateTime}
	  		
	  		,REMARK = #{remark}
	  		
	  		
	  		,CSR_NAME = #{csrName}
	  			        
		WHERE
			ID=#{id}

mapper.select:
	  	SELECT
	  		 ID as id,
	  		 CP_CODE as cpCode,
	  		 CSR_TYPE as csrType,
	         CSR_IDENTIFER as csrIdentifer,
	         CSR_CODE as csrCode,
	         REG_ADDRESS as regAddress,
	         OFFICE_ADDRESS as officeAddress,
	         CSR_OWNER as csrOwner,
	         FINANCE_LEADER as financeLeader,
	         TAX_LEADER as taxLeader,
	         OPERATOR as operator,
	         CREATE_TIME as createTime,
	         UPDATE_TIME as updateTime,
	         REMARK as remark,
	         CSR_NAME as csrName
	    FROM AT_CSR_BASIC  
	    	WHERE 1 = 1
	    	
	    	and CP_CODE = #{cpCode}
	    	
	    	
    		and CSR_TYPE = #{csrType}
    		
	  		
	  	    and CSR_IDENTIFER = #{csrIdentifer}
	  		
	  		
	  		and CSR_CODE = #{csrCode}
	  		
	  		
	  		and REG_ADDRESS = #{regAddress}
	  		
	  		
	  		and OFFICE_ADDRESS = #{officeAddress}
	  		
	  		
	  		and CSR_OWNER = #{csrOwner}
	  		
	  		
	  		and FINANCE_LEADER = #{financeLeader}
	  		
	  		
	  		and TAX_LEADER = #{taxLeader}
	  		
	  		
	  		and OPERATOR = #{operator}
	  		
	  		
	  		and REMARK = #{remark}
	  		
	  		
	  		and CSR_NAME = #{csrName}
	  			
	    	ORDER BY CREATE_TIME DESC 
	    	limit #{page.pageSize} offset #{page.offset}

SELECT ID as id, CP_CODE as cpCode, CSR_TYPE as csrType, CSR_IDENTIFER as csrIdentifer, CSR_CODE as csrCode, REG_ADDRESS as regAddress, OFFICE_ADDRESS as officeAddress, CSR_OWNER as csrOwner, FINANCE_LEADER as financeLeader, TAX_LEADER as taxLeader, OPERATOR as operator, CREATE_TIME as createTime, UPDATE_TIME as updateTime, REMARK as remark, CSR_NAME as csrName FROM AT_CSR_BASIC WHERE OPERATOR = #{userId}
and CSR_IDENTIFER = #{csrIdentifer}
limit #{page.pageSize} offset #{page.offset}

  	SELECT
  		 count(*)
    FROM AT_CSR_BASIC  
    WHERE 
    OPERATOR = #{userId}
    
	  	and CSR_IDENTIFER = #{csrIdentifer}

SELECT ID as id, CP_CODE as cpCode, CSR_TYPE as csrType, CSR_IDENTIFER as csrIdentifer, CSR_CODE as csrCode, REG_ADDRESS as regAddress, OFFICE_ADDRESS as officeAddress, CSR_OWNER as csrOwner, FINANCE_LEADER as financeLeader, TAX_LEADER as taxLeader, OPERATOR as operator, CREATE_TIME as createTime, UPDATE_TIME as updateTime, REMARK as remark, CSR_NAME as csrName FROM AT_CSR_BASIC WHERE CP_CODE = #{cpCode}
and CSR_IDENTIFER like #{csrIdentifer} or CSR_NAME like #{csrName}
limit #{page.pageSize} offset #{page.offset}

  	SELECT
  		 count(*)
    FROM AT_CSR_BASIC  
    WHERE 
    CP_CODE = #{cpCode}
    
	  	and CSR_IDENTIFER like #{csrIdentifer}
	  	or CSR_NAME like #{csrName}

SELECT ID as id, CP_CODE as cpCode, CSR_TYPE as csrType, CSR_IDENTIFER as csrIdentifer, CSR_CODE as csrCode, REG_ADDRESS as regAddress, OFFICE_ADDRESS as officeAddress, CSR_OWNER as csrOwner, FINANCE_LEADER as financeLeader, TAX_LEADER as taxLeader, OPERATOR as operator, CREATE_TIME as createTime, UPDATE_TIME as updateTime, REMARK as remark, CSR_NAME as csrName FROM AT_CSR_BASIC b WHERE EXISTS ( select u.id from AT_USER u where u.oid = #{orgId} and b.OPERATOR = u.id )
and CSR_IDENTIFER like #{csrIdentifer} or CSR_NAME like #{csrName}
limit #{page.pageSize} offset #{page.offset}

  	SELECT
  		 count(*)
    FROM AT_CSR_BASIC b
    WHERE 
    EXISTS 
    (
      select u.id from AT_USER u where u.oid = #{orgId}
      and
      b.OPERATOR = u.id
    )
    
	  	and CSR_IDENTIFER like #{csrIdentifer}
	  	or CSR_NAME like #{csrName}

	    SELECT
	         count(*)
	    FROM AT_CSR_BASIC 
	    WHERE 1 = 1
	    	
	    	and CP_CODE = #{cpCode}
	    	
	   		
    		and CSR_TYPE = #{csrType}
    		
	  		
	  	    and CSR_IDENTIFER = #{csrIdentifer}
	  		
	  		
	  		and CSR_CODE = #{csrCode}
	  		
	  		
	  		and REG_ADDRESS = #{regAddress}
	  		
	  		
	  		and OFFICE_ADDRESS = #{officeAddress}
	  		
	  		
	  		and CSR_OWNER = #{csrOwner}
	  		
	  		
	  		and FINANCE_LEADER = #{financeLeader}
	  		
	  		
	  		and TAX_LEADER = #{taxLeader}
	  		
	  		
	  		and OPERATOR = #{operator}
	  		
	  		
	  		and REMARK = #{remark}
	  		
	  		
	  		and CSR_NAME = #{csrName}
