mapper.update:
  	UPDATE AT_FIELD_REL
  		SET
  			UPDATE_TIME = #{updateTime}
  	    	,MENU_CODE = #{menuCode}
  	    	,FID = #{fid}
  	    	,UID = #{uid}

  	    	,R_NAME = #{rname}


  	    	,PRIVILEGETYPE = #{privilegeType}


  	    	,ISUSERGROUP = #{isUserGroup}


  	    	,OPERATOR = #{operator}


  			,REMARK = #{remark}

		WHERE
			ID=#{id}

mapper.delete:
DELETE FROM AT_FIELD_REL WHERE 1 = 1 

    	and UID = #{uid}


    	and MENU_CODE in 
    	(
    		select asm.MENU_CODE from AT_SYS_MODEL asm 
    		where asm.TREE_ID = #{treeId}
    	)

mapper.select:
  	SELECT
  		 c.ID as id,
  		 MENU_CODE as menuCode,
         R_NAME as rname,
         PRIVILEGETYPE as privilegeType,
         FID as fid,
		 UID as uid,
		 ISUSERGROUP as isUserGroup,
         OPERATOR as operator,
         CREATE_TIME as createTime,
	     UPDATE_TIME as updateTime,
         REMARK as remark
    FROM AT_FIELD_REL c 
    	where 1 = 1

 	    	and MENU_CODE = #{menuCode}


 	    	and R_NAME like #{rname}


 	    	and PRIVILEGETYPE = #{privilegeType}


 	    	and FID = #{fid}


 	    	and UID = #{uid}


  	    	and REMARK like #{remark}

    	ORDER BY createTime DESC
    	limit #{page.pageSize} offset #{page.offset}

 	SELECT COUNT(*) FROM AT_FIELD_REL c
 		where 1 = 1

	    	and MENU_CODE = #{menuCode}


	    	and R_NAME like #{rName}


	    	and PRIVILEGETYPE = #{privilegeType}


	    	and FID = #{fid}


	    	and UID = #{uid}


 	    	and REMARK like #{remark}

SELECT c.ID as id, MENU_CODE as menuCode, R_NAME as rname, PRIVILEGETYPE as privilegeType, FID as fid, UID as uid, ISUSERGROUP as isUserGroup, OPERATOR as operator, CREATE_TIME as createTime, UPDATE_TIME as updateTime, REMARK as remark FROM AT_FIELD_REL c where 1 = 1
and UID = #{uid}
and MENU_CODE like #{menuCode}
ORDER BY createTime
