mapper.update:
  	UPDATE AT_SYS_MENU
  		SET
            M_TITLE = #{mtitle}
            ,M_DESC = #{mdesc}	 
            ,PARENT_ID = #{parentId}  
            ,TFLAG = #{tflag} 
            ,INID = #{inid}
            ,IS_SYSTEM = #{idSystem}  
            ,CATEGORY = #{category}  
            ,IS_ENDNODE = #{isEndnode}
            ,SHOW_INDEX = #{showIndex}
            ,UPDATE_TIME = #{updateTime}
            ,REMARK = #{remark}
		WHERE
			ID=#{id}

mapper.select:
	SELECT
		 ID as id,
		 M_TITLE as mtitle,
		 M_DESC as mdesc,
		 PARENT_ID as parentId,
		 TFLAG as tflag,
		 INID as inid,
		 IS_SYSTEM as isSystem,
		 CATEGORY as category,
		 IS_ENDNODE as isEndnode,
		 SHOW_INDEX as showIndex,
       CREATE_TIME as createTime,
       UPDATE_TIME as updateTime,
       REMARK as remark
  FROM AT_SYS_MENU  
  	WHERE 1 = 1
  	and ID = #{id}
  	and M_TITLE = #{mTitle}
  	and M_DESC = #{mDesc}
  	and PARENT_ID = #{parentId}
  	and TFLAG = #{tflag}
  	and INID = #{inid}
  	and IS_SYSTEM = #{isSystem}
  	and CATEGORY = #{category}
  	and IS_ENDNODE = #{isEndnode}
  	and SHOW_INDEX = #{showIndex}
  	and REMARK = #{remark}
  	ORDER BY CREATE_TIME DESC 
  	limit #{page.pageSize} offset #{page.offset}

 SELECT
      count(*)
 FROM AT_SYS_MENU 
 WHERE 1 = 1
and ID = #{id}
 	and M_TITLE = #{mTitle}
 	and M_DESC = #{mDesc}
 	and PARENT_ID = #{parentId}
 	and TFLAG = #{tflag}
 	and INID = #{inid}
 	and IS_SYSTEM = #{isSystem}
 	and CATEGORY = #{category}
 	and IS_ENDNODE = #{isEndnode}
 	and SHOW_INDEX = #{showIndex}
 	and REMARK = #{remark}
