mapper.update:
  	UPDATE AT_SYS_MODEL
  		SET
  			UPDATE_TIME = #{updateTime}
  	    	,TREE_ID = #{treeId}
  	    	,MENU_CODE = #{menuCode}
  	    	,M_NAME = #{mname}
  		
  	    	,M_DESC = #{mdesc}
  		
  		
  	    	,IS_CON_SHOWNUM = #{isConShownum}
  		
  		
  	    	,CONTROL_NUM = #{controlNum}
  		
  		
  	    	,CONTROL_PAGE = #{controlPage}
  		
  		
  	    	,SHOW_INDEX = #{showIndex}
  		
  		
  			,REMARK = #{remark}
  			        
		WHERE
			ID=#{id}

mapper.select:
SELECT c.ID as id, TREE_ID as treeId, MENU_CODE as menuCode, M_NAME as mname, M_DESC as mdesc, IS_SYSTEM as isSystem, CATEGORY as category, IS_CON_SHOWNUM as isConShownum, CONTROL_NUM as controlNum, CONTROL_PAGE as controlPage, SHOW_INDEX as showIndex, CREATE_TIME as createTime, UPDATE_TIME as updateTime, REMARK as remark FROM AT_SYS_MODEL c where 1 = 1
and TREE_ID = #{treeId}
and MENU_CODE = #{menuCode}
and LOCATE(#{mname},M_NAME) > 0
and IS_CON_SHOWNUM = #{isConShownum}
and LOCATE(#{remark},REMARK) > 0
ORDER BY showIndex limit #{page.pageSize} offset #{page.offset}

  	SELECT COUNT(*) FROM AT_SYS_MODEL c
  		where 1 = 1
  		
	    	and TREE_ID = #{treeId}
 		
 		
 	    	and MENU_CODE = #{menuCode}
 		
 		
 	    	and LOCATE(#{mname},M_NAME) > 0
 		
  		
  	    	and IS_CON_SHOWNUM = #{isConShownum}
  		
  		
  	    	and LOCATE(#{remark},REMARK) > 0
