mapper.insert:
INSERT INTO AT_USER_GROUP_REL(
     UID,
     GID,
     REMARK
	 )
VALUES(
	 #{uid},
     #{gid},
     #{remark}
	   )

mapper.update:
  	UPDATE AT_USER_GROUP_REL
  		SET
	  		UID = #{uid}
	  		,GID = #{gid}
	  		,UPDATE_TIME = #{updateTime}
	  		
	  		  ,REMARK = #{remark}
	  		
		WHERE
			ID=#{id}

mapper.select:
	SELECT
		ID as id,
		UID as uid,
		GID as gid,
		CREATE_TIME as createTime,
		UPDATE_TIME as updateTime,
		REMARK as remark 
  FROM AT_USER_GROUP_REL 
  	WHERE 1 = 1
  	and UID = #{uid}
  	and GID = #{gid}
  	and CREATE_TIME = #{createTime}
  	and UPDATE_TIME = #{updateTime}
  	and REMARK = #{remark}
  	ORDER BY CREATE_TIME DESC 
  	limit #{page.pageSize} offset #{page.offset}

	SELECT
		ID as id,
		UID as uid,
		GID as gid,
		CREATE_TIME as createTime,
		UPDATE_TIME as updateTime,
		REMARK as remark 
  FROM AT_USER_GROUP_REL 
  	WHERE 1 = 1
  	and UID = #{uid}
  	and GID = #{gid}
  	and CREATE_TIME = #{createTime}
  	and UPDATE_TIME = #{updateTime}
  	and REMARK = #{remark}

SELECT
     count(*)
FROM AT_USER_GROUP_REL 
WHERE 1 = 1
and UID = #{uid}
	and GID = #{gid}
	and CREATE_TIME = #{createTime}
	and UPDATE_TIME = #{updateTime}
	and REMARK = #{remark}
