mapper.update:
UPDATE OCS_DEV_APP_LIST SET USER_NAME = #{userName,jdbcType=VARCHAR}, USER_PASSWD = #{userPasswd,jdbcType=VARCHAR},
USER_STATUS = #{userStatus,jdbcType=DECIMAL},
USER_ROLE = #{userRole,jdbcType=DECIMAL},
USER_CREATETIME = #{userCreateTime},
REMARK = #{remark,jdbcType=VARCHAR}
WHERE ID=#{id}

mapper.select:
SELECT c.ID as id, DEV_ID as devId, APP_KEY as appKey, APP_NAME as appName, APP_TYPE as appType, DIVIDE_SCALE as divideScale, APP_STATUS as appStatus, CREATE_TIME as createTime, UPDATE_TIME as updateTime, REMARK as remark FROM OCS_DEV_APP_LIST c WHERE 1 = 1
and c.APP_NAME = #{appName}
and c.APP_TYPE = #{appType}
and c.APP_STATUS=#{appStatus}
ORDER BY createTime DESC limit #{page.pageSize} offset #{page.offset}

SELECT c.ID as id, DEV_ID as devId, APP_KEY as appKey, APP_NAME as appName, APP_TYPE as appType, DIVIDE_SCALE as divideScale, APP_STATUS as appStatus, CREATE_TIME as createTime, UPDATE_TIME as updateTime, REMARK as remark, RID as rid, PACKAGE_NAME as pagName FROM APP_RLN_VIEW c WHERE 1 = 1
and c.APP_NAME = #{appName}
and c.APP_TYPE = #{appType}
and c.APP_STATUS=#{appStatus}
ORDER BY createTime DESC limit #{page.pageSize} offset #{page.offset}

